mapper.select:
select * from t_book b,t_book_favorites_relationship f
where f.favorites_id = #{favoritesId} and f.book_id = b.book_id
limit ${(pageIndex-1)*pageSize},${pageSize}

mapper.update:
update t_book
book_category_id=#{bookCategoryId}, title=#{title}, `desc`=#{desc}, cover=#{cover}, author=#{author}, publisher=#{publisher}, pubdate=#{pubdate}, favorites_num=#{favoritesNum}, evaluation_num=#{evaluationNum}, recommend_num=#{recommendNum}
where book_id=#{bookId}
